/**
 * Motion & Transitions
 * 
 * Animations:
 * - Fade-in (opacity)
 * - Slide-up (transform + opacity)
 * - Scale (transform + opacity)
 * - Stagger (delay-based)
 * 
 * Page Transitions:
 * - Route transitions (fade + slide)
 * - Modal transitions (scale + fade)
 * 
 * Respects:
 * - prefers-reduced-motion
 * - Design tokens (duration, easing)
 */
/* === Keyframes === */
@keyframes fade-in {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}
@keyframes slide-up {
  from {
    opacity: 0;
    transform: translateY(16px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}
@keyframes slide-down {
  from {
    opacity: 0;
    transform: translateY(-16px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}
@keyframes scale-in {
  from {
    opacity: 0;
    transform: scale(0.95);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
}
@keyframes slide-in-left {
  from {
    opacity: 0;
    transform: translateX(-40px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}
@keyframes slide-in-right {
  from {
    opacity: 0;
    transform: translateX(40px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}
@keyframes glow-pulse {
  0%, 100% {
    box-shadow: 0 0 12px rgba(15, 179, 76, 0.3);
  }
  50% {
    box-shadow: 0 0 32px rgba(15, 179, 76, 0.55);
  }
}
@keyframes float {
  0%, 100% {
    transform: translateY(0);
  }
  50% {
    transform: translateY(-10px);
  }
}
@keyframes ticker {
  0% {
    transform: translateX(0);
  }
  100% {
    transform: translateX(-50%);
  }
}
@keyframes shimmer {
  0% {
    background-position: -200% 0;
  }
  100% {
    background-position: 200% 0;
  }
}
@keyframes gradient-shift {
  0%, 100% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
}
/* === Utility Classes === */
.animate-fade-in {
  animation: fade-in var(--duration-medium) var(--ease-in-out) forwards;
}
.animate-slide-up {
  animation: slide-up var(--duration-medium) var(--ease-out) forwards;
}
.animate-slide-down {
  animation: slide-down var(--duration-medium) var(--ease-out) forwards;
}
.animate-scale-in {
  animation: scale-in var(--duration-short) var(--ease-out) forwards;
}
.animate-slide-in-left {
  animation: slide-in-left var(--duration-medium) var(--ease-out) forwards;
}
.animate-slide-in-right {
  animation: slide-in-right var(--duration-medium) var(--ease-out) forwards;
}
.animate-shimmer {
  background: linear-gradient(
    90deg,
    rgba(255, 255, 255, 0) 0%,
    rgba(255, 255, 255, 0.05) 50%,
    rgba(255, 255, 255, 0) 100%
  );
  background-size: 200% 100%;
  animation: shimmer 1.5s infinite;
}
.animate-glow-pulse {
  animation: glow-pulse 2s ease-in-out infinite;
}
.animate-float {
  animation: float 3s ease-in-out infinite;
  will-change: transform;
}
.animate-ticker {
  animation: ticker 30s linear infinite;
  will-change: transform;
}
.animate-gradient-shift {
  background-size: 200% auto;
  animation: gradient-shift 3s ease-in-out infinite;
}
/* === Stagger Effects (for lists) === */
.stagger > * {
  opacity: 0;
  animation: slide-up var(--duration-medium) var(--ease-out) forwards;
}
.stagger > *:nth-child(1) { animation-delay: 0ms; }
.stagger > *:nth-child(2) { animation-delay: 50ms; }
.stagger > *:nth-child(3) { animation-delay: 100ms; }
.stagger > *:nth-child(4) { animation-delay: 150ms; }
.stagger > *:nth-child(5) { animation-delay: 200ms; }
.stagger > *:nth-child(6) { animation-delay: 250ms; }
.stagger > *:nth-child(7) { animation-delay: 300ms; }
.stagger > *:nth-child(8) { animation-delay: 350ms; }
.stagger > *:nth-child(9) { animation-delay: 400ms; }
.stagger > *:nth-child(10) { animation-delay: 450ms; }
@layer utilities {
  .motion-delay-0 {
    animation-delay: 0s !important;
    transition-delay: 0s !important;
  }

  .motion-delay-100 {
    animation-delay: 0.1s;
    transition-delay: 0.1s;
  }

  .motion-delay-200 {
    animation-delay: 0.2s;
    transition-delay: 0.2s;
  }

  .motion-delay-300 {
    animation-delay: 0.3s;
    transition-delay: 0.3s;
  }

  .motion-delay-400 {
    animation-delay: 0.4s;
    transition-delay: 0.4s;
  }

  .motion-delay-500 {
    animation-delay: 0.5s;
    transition-delay: 0.5s;
  }

  .motion-delay-600 {
    animation-delay: 0.6s;
    transition-delay: 0.6s;
  }

  .motion-delay-700 {
    animation-delay: 0.7s;
    transition-delay: 0.7s;
  }

  .motion-delay-800 {
    animation-delay: 0.8s;
    transition-delay: 0.8s;
  }

  .motion-delay-900 {
    animation-delay: 0.9s;
    transition-delay: 0.9s;
  }

  .motion-delay-1000 {
    animation-delay: 1s;
    transition-delay: 1s;
  }

  .motion-delay-1200 {
    animation-delay: 1.2s;
    transition-delay: 1.2s;
  }

  .motion-duration-fast {
    animation-duration: var(--duration-short) !important;
    transition-duration: var(--duration-short) !important;
  }

  .motion-duration-base {
    animation-duration: var(--duration-medium) !important;
    transition-duration: var(--duration-medium) !important;
  }

  .motion-duration-slow {
    animation-duration: var(--duration-long) !important;
    transition-duration: var(--duration-long) !important;
  }
}
/* === Page Transitions === */
.page-enter {
  opacity: 0;
  transform: translateY(16px);
}
.page-enter-active {
  opacity: 1;
  transform: translateY(0);
  transition: opacity var(--duration-long) var(--ease-out),
              transform var(--duration-long) var(--ease-out);
}
.page-exit {
  opacity: 1;
  transform: translateY(0);
}
.page-exit-active {
  opacity: 0;
  transform: translateY(-8px);
  transition: opacity var(--duration-medium) var(--ease-in),
              transform var(--duration-medium) var(--ease-in);
}
/* === Modal Transitions === */
.modal-enter {
  opacity: 0;
  transform: scale(0.95);
}
.modal-enter-active {
  opacity: 1;
  transform: scale(1);
  transition: opacity var(--duration-medium) var(--ease-out),
              transform var(--duration-medium) var(--ease-out);
}
.modal-exit {
  opacity: 1;
  transform: scale(1);
}
.modal-exit-active {
  opacity: 0;
  transform: scale(0.95);
  transition: opacity var(--duration-short) var(--ease-in),
              transform var(--duration-short) var(--ease-in);
}
/* === Backdrop Transitions === */
.backdrop-enter {
  opacity: 0;
}
.backdrop-enter-active {
  opacity: 1;
  transition: opacity var(--duration-medium) var(--ease-out);
}
.backdrop-exit {
  opacity: 1;
}
.backdrop-exit-active {
  opacity: 0;
  transition: opacity var(--duration-medium) var(--ease-in);
}
/* === Reduced Motion Override === */
@media (prefers-reduced-motion: reduce) {
  *,
  *::before,
  *::after {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
  }

  .animate-fade-in,
  .animate-slide-up,
  .animate-slide-down,
  .animate-scale-in,
  .animate-slide-in-left,
  .animate-slide-in-right,
  .animate-glow-pulse,
  .animate-float,
  .animate-gradient-shift,
  .stagger > * {
    opacity: 1;
    transform: none;
    animation: none;
  }

  .animate-ticker {
    animation-duration: 45s;
    animation-iteration-count: 1;
    transform: none;
  }

  [class*="motion-delay-"] {
    animation-delay: 0s !important;
    transition-delay: 0s !important;
  }
}
/* === Hover Transitions === */
.hover-lift {
  transition: transform var(--duration-short) var(--ease-out),
              box-shadow var(--duration-short) var(--ease-out);
}
.hover-lift:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 16px rgba(0, 0, 0, 0.3);
}
.hover-lift:active {
  transform: translateY(0);
}
/* === Focus Animations === */
.focus-ring {
  outline: 2px solid transparent;
  outline-offset: 2px;
  transition: outline-color var(--duration-short) var(--ease-in-out);
}
.focus-ring:focus-visible {
  outline-color: rgb(var(--color-emerald));
}
/* === Loading Spinner === */
@keyframes spin {
  to {
    transform: rotate(360deg);
  }
}
.animate-spin {
  animation: spin 1s linear infinite;
}
/* === Pulse (for loading states) === */
@keyframes pulse {
  0%, 100% {
    opacity: 1;
  }
  50% {
    opacity: 0.5;
  }
}
.animate-pulse {
  animation: pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
}
/**
 * High Contrast Mode
 * 
 * Enhances visibility for users with:
 * - Low vision
 * - Color blindness
 * - High ambient light conditions
 * 
 * Activated by:
 * - Windows High Contrast Mode
 * - prefers-contrast: high media query
 * - Manual toggle (future)
 * 
 * Features:
 * - Stronger borders and outlines
 * - Higher color contrast ratios
 * - Simplified color palette
 * - Bolder text weights
 */
/* === High Contrast Mode (Auto-detect) === */
@media (prefers-contrast: high) {
  :root {
    /* Stronger colors */
    --color-emerald: 0 255 0; /* Brighter green */
    --color-rose: 255 0 0; /* Pure red */
    --color-amber: 255 170 0; /* Brighter amber */
    --color-cyan: 0 255 255; /* Bright cyan */
    --color-bg: 0 0 0;
    --color-bg-elevated: 0 0 0;
    --color-surface: 12 12 12;
    --color-surface-subtle: 18 18 18;
    --color-surface-elevated: 24 24 24;
    --color-surface-hover: 38 38 38;

    /* Higher contrast text */
    --color-text-primary: 255 255 255;
    --color-text-secondary: 224 224 224;
    --color-text-tertiary: 204 204 204;

    /* Stronger borders */
    --color-border: 255 255 255;
    --color-border-secondary: 204 204 204;
  }

  /* === Borders === */

  * {
    /* Ensure all interactive elements have visible borders */
    border-width: 2px !important;
  }

  button,
  a,
  input,
  select,
  textarea,
  [role="button"] {
    border: 2px solid rgb(var(--color-border)) !important;
  }

  /* === Focus Indicators === */

  *:focus-visible {
    outline: 3px solid rgb(var(--color-emerald)) !important;
    outline-offset: 2px !important;
  }

  /* === Text === */

  body {
    font-weight: 500 !important;
  }

  h1, h2, h3, h4, h5, h6 {
    font-weight: 700 !important;
  }

  /* === Interactive Elements === */

  button:hover,
  a:hover,
  [role="button"]:hover {
    background-color: rgb(var(--color-surface-hover)) !important;
    border-color: rgb(var(--color-emerald)) !important;
  }

  button:active,
  a:active,
  [role="button"]:active {
    background-color: rgb(var(--color-surface)) !important;
  }

  button:disabled,
  [disabled] {
    opacity: 0.5 !important;
    cursor: not-allowed !important;
  }

  /* === Status Colors === */

  .text-brand,
  .text-success,
  .bg-brand {
    color: rgb(var(--color-emerald)) !important;
    background-color: transparent !important;
    border-color: rgb(var(--color-emerald)) !important;
  }

  .text-danger,
  .bg-danger {
    color: rgb(var(--color-rose)) !important;
    background-color: transparent !important;
    border-color: rgb(var(--color-rose)) !important;
  }

  .text-warn,
  .bg-warn {
    color: rgb(var(--color-amber)) !important;
    background-color: transparent !important;
    border-color: rgb(var(--color-amber)) !important;
  }

  /* === Icons === */

  svg {
    stroke-width: 2.5 !important;
  }

  /* === Shadows (Remove) === */

  * {
    box-shadow: none !important;
    text-shadow: none !important;
  }

  /* === Images === */

  img {
    border: 2px solid rgb(var(--color-border)) !important;
  }
}
/* === Windows High Contrast Mode === */
@media screen and (-ms-high-contrast: active) {
  /* Use system colors */
  * {
    background-color: Window !important;
    color: WindowText !important;
    border-color: WindowText !important;
  }

  a,
  button,
  [role="button"] {
    color: LinkText !important;
  }

  a:hover,
  button:hover,
  [role="button"]:hover {
    color: HighlightText !important;
    background-color: Highlight !important;
  }

  *:focus-visible {
    outline: 3px solid Highlight !important;
  }
}
/* === Manual High Contrast Toggle === */
[data-contrast="high"] {
  /* Same styles as prefers-contrast: high */
  /* Users can toggle this via Settings */

  /* Stronger colors */
  --color-emerald: 0 255 0;
  --color-rose: 255 0 0;
  --color-amber: 255 170 0;
  --color-cyan: 0 255 255;
  --color-bg: 0 0 0;
