    50% {
      opacity: 0.7;
      box-shadow: 0 0 16px rgba(var(--color-brand) / 0.6);
    }
  }
  .shimmer {
    background: linear-gradient(
      90deg,
      rgba(var(--color-surface-skeleton) / 0.05) 0%,
      rgba(var(--color-surface-skeleton) / 0.1) 50%,
      rgba(var(--color-surface-skeleton) / 0.05) 100%
    );
    background-size: 200% 100%;
    animation: shimmer 1.5s infinite;
  }
  @keyframes shimmer {
    0% {
      background-position: -200% 0;
    }
    100% {
      background-position: 200% 0;
    }
  }
  .scrollbar-hide {
    -ms-overflow-style: none;
    scrollbar-width: none;
  }
  .scrollbar-hide::-webkit-scrollbar {
    display: none;
  }
  .scrollbar-custom {
    scrollbar-width: thin;
    scrollbar-color: rgb(var(--color-border)) transparent;
  }
  .scrollbar-custom::-webkit-scrollbar {
    width: 6px;
    height: 6px;
  }
  .scrollbar-custom::-webkit-scrollbar-track {
    background: transparent;
  }
  .scrollbar-custom::-webkit-scrollbar-thumb {
    background: rgb(var(--color-border));
    border-radius: 3px;
  }
