  .text-gradient-brand {
    background: linear-gradient(
      135deg,
      rgb(var(--color-brand)),
      rgb(var(--color-cyan))
    );
    -webkit-background-clip: text;
    background-clip: text;
    -webkit-text-fill-color: transparent;
  }
  .text-gradient-success {
    background: linear-gradient(
      135deg,
      rgb(var(--color-success)),
      rgb(var(--color-info))
    );
    -webkit-background-clip: text;
    background-clip: text;
    -webkit-text-fill-color: transparent;
  }
  .text-gradient-danger {
    background: linear-gradient(
      135deg,
      rgb(var(--color-danger)),
      rgba(var(--color-danger) / 0.7)
    );
    -webkit-background-clip: text;
    background-clip: text;
    -webkit-text-fill-color: transparent;
  }
  .backdrop-blur-xs {
    backdrop-filter: blur(2px);
  }
  .backdrop-blur-sm {
    backdrop-filter: blur(4px);
  }
  .backdrop-blur-md {
    backdrop-filter: blur(8px);
  }
  .backdrop-blur-lg {
    backdrop-filter: blur(12px);
  }
  .backdrop-blur-xl {
    backdrop-filter: blur(16px);
  }
  .border-glow-brand {
    border: 1px solid rgb(var(--color-brand) / 0.3);
    box-shadow: 0 0 12px rgba(var(--color-brand) / 0.2);
  }
  .border-glow-success {
    border: 1px solid rgb(var(--color-success) / 0.3);
    box-shadow: 0 0 12px rgba(var(--color-success) / 0.2);
  }
  .border-glow-danger {
    border: 1px solid rgb(var(--color-danger) / 0.3);
    box-shadow: 0 0 12px rgba(var(--color-danger) / 0.2);
  }
  .bg-grid-pattern {
    background-image: linear-gradient(rgba(var(--color-border-contrast) / 0.05) 1px, transparent 1px),
      linear-gradient(90deg, rgba(var(--color-border-contrast) / 0.05) 1px, transparent 1px);
    background-size: 20px 20px;
  }
  .bg-grid-pattern-lg {
    background-image: linear-gradient(rgba(var(--color-border-contrast) / 0.05) 1px, transparent 1px),
      linear-gradient(90deg, rgba(var(--color-border-contrast) / 0.05) 1px, transparent 1px);
    background-size: 40px 40px;
  }
  .line-clamp-1 {
    display: -webkit-box;
    -webkit-line-clamp: 1;
    -webkit-box-orient: vertical;
    overflow: hidden;
  }
  .line-clamp-2 {
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    overflow: hidden;
  }
  .line-clamp-3 {
    display: -webkit-box;
    -webkit-line-clamp: 3;
    -webkit-box-orient: vertical;
    overflow: hidden;
  }
  .line-clamp-4 {
    display: -webkit-box;
    -webkit-line-clamp: 4;
    -webkit-box-orient: vertical;
    overflow: hidden;
  }
  .bg-surface-skeleton {
    background-color: rgb(var(--color-surface) / 0.5);
  }
  .input {
    width: 100%;
    padding: 0.625rem 1rem;
    border-radius: var(--radius-lg);
    border: 1px solid rgb(var(--color-border));
    background-color: rgb(var(--color-surface));
    color: rgb(var(--color-text-primary));
    font-size: 0.875rem;
    transition: all 180ms ease-out;
  }
  .input:hover {
    border-color: rgba(var(--color-border-contrast) / 0.15);
  }
  .input:focus {
    outline: none;
    border-color: rgb(var(--color-brand));
    box-shadow: 0 0 0 3px rgba(var(--color-brand) / 0.1);
  }
  .input::-moz-placeholder {
    color: rgb(var(--color-text-tertiary));
  }
  .input::placeholder {
    color: rgb(var(--color-text-tertiary));
  }
  body.keyboard-user *:focus {
    outline: 2px solid rgb(var(--color-brand));
    outline-offset: 2px;
  }
}
