/**
 * Driver.js Theme Override - Sparkfined Dark Theme
 */

/* Popover Background */
.driver-popover {
  background: rgb(var(--color-surface)) !important;
  color: rgb(var(--color-text-primary)) !important;
  border: 1px solid rgb(var(--color-border)) !important;
  box-shadow: 0 20px 25px -5px rgb(0 0 0 / 0.5), 0 8px 10px -6px rgb(0 0 0 / 0.5) !important;
}

/* Title */
.driver-popover-title {
  color: rgb(var(--color-text-primary)) !important;
  font-weight: 600 !important;
  font-size: 1.125rem !important;
}

/* Description */
.driver-popover-description {
  color: rgb(var(--color-text-secondary)) !important;
  line-height: 1.5 !important;
}

/* Progress Text */
.driver-popover-progress-text {
  color: rgb(var(--color-text-tertiary)) !important;
  font-size: 0.875rem !important;
}

/* Buttons */
.driver-popover-next-btn,
.driver-popover-prev-btn,
.driver-popover-close-btn {
  transition: all 150ms ease-in-out !important;
  border-radius: 0.5rem !important;
  padding: 0.5rem 1rem !important;
  font-weight: 500 !important;
}

/* Next/Done Button */
.driver-popover-next-btn {
  background: rgb(var(--color-brand)) !important;
  color: rgb(var(--color-bg)) !important;
  border: none !important;
  box-shadow: 0 1px 2px 0 rgb(0 0 0 / 0.05) !important;
}

.driver-popover-next-btn:hover {
  background: rgb(var(--color-brand-hover)) !important;
  transform: scale(1.05) !important;
}

.driver-popover-next-btn:active {
  transform: scale(0.98) !important;
}

/* Previous Button */
.driver-popover-prev-btn {
  background: rgb(var(--color-surface-hover)) !important;
  color: rgb(var(--color-text-primary)) !important;
  border: 1px solid rgb(var(--color-border)) !important;
}

.driver-popover-prev-btn:hover {
  background: rgb(var(--color-surface-elevated)) !important;
  border-color: rgba(var(--color-border-contrast) / 0.25) !important;
}

/* Close Button */
.driver-popover-close-btn {
  background: transparent !important;
  color: rgb(var(--color-text-secondary)) !important;
  border: none !important;
}

.driver-popover-close-btn:hover {
  color: rgb(var(--color-text-primary)) !important;
  background: rgb(var(--color-surface-hover)) !important;
}

/* Arrow */
.driver-popover-arrow-side-left.driver-popover-arrow,
.driver-popover-arrow-side-right.driver-popover-arrow,
.driver-popover-arrow-side-top.driver-popover-arrow,
.driver-popover-arrow-side-bottom.driver-popover-arrow {
  border-color: rgb(var(--color-surface)) !important;
}

/* Overlay */
.driver-overlay {
  background-color: rgba(var(--color-bg) / 0.8) !important;
  backdrop-filter: blur(4px) !important;
}

/* Highlighted element */
.driver-active-element {
  outline: 2px solid rgb(var(--color-brand)) !important;
  outline-offset: 4px !important;
}

/* Keyboard shortcuts in description */
.driver-popover-description kbd {
  background: rgb(var(--color-surface-hover));
  padding: 2px 6px;
  border-radius: 4px;
  font-family: monospace;
  font-size: 0.875em;
  border: 1px solid rgb(var(--color-border));
}
